import React, { useState } from 'react'
import Logo from '../assets/vector.png';
import Cart from '../assets/shopping cart.png'
import {Link} from "react-router-dom";
import '../styles/navbar.css'
function Navbar() {

  //State for Toggling the menu 
  const[menuOpen,setMenuOpen] = useState(false);

  //Function to toggle the menu open state
  const toggleMenu = () =>{
    setMenuOpen(!menuOpen);
  };
  return (
    <div className='navbar'>
        <div className='leftSide'>
          <div className='logos'>
            <img src={Logo} alt='img' />
            <div className='txtheader'>
            <Link to ="/Home"><h1>One Bite Food</h1></Link>
            
            </div>
            
            </div>
        </div>
        {/* Hamburger menu */}
        <input type="checkbox" id="menu-toggle" className="menu-toggle" checked={menuOpen} onChange={toggleMenu} />
      <label htmlFor="menu-toggle" className="menu-icon" onClick={toggleMenu}>
        <span></span>
        <span></span>
        <span></span>
      </label>

      <div className={menuOpen ? 'middle open' : 'middle'}>
            <Link to ="/Menu">Menu</Link>
            <Link to ="/Story">Our Story</Link>
            <Link to ="/Location">Location</Link>
            <Link to ="/Tables">Tables</Link>
            </div>
        <div className='rightSide'>
          <div className='logo-buttons'>
            <img src={Cart} alt='img'/>
            <div className='txtbtn'>
              <button className='book-btn'>
              <Link to ="/table">
              <h3>Book a Table</h3></Link>
              </button>
              
            
            </div>
            
            </div>
        </div>
    </div>
  );
}

export default Navbar
